// src/TypingAnimation.js
import React, { useState, useEffect } from "react";
import "./TypingAnimation.css";

const TypingAnimation = () => {
  const sentences = ["Welcome to the Typing Animation in React!", "This sentence will be erased and replaced.", "Enjoy learning React.js!"];

  const [text, setText] = useState("");
  const [index, setIndex] = useState(0); // Index for the current character
  const [sentenceIndex, setSentenceIndex] = useState(0); // Index for the current sentence
  const [isDeleting, setIsDeleting] = useState(false); // Whether the text is being deleted
  const [speed, setSpeed] = useState(150); // Speed of typing and deleting

  useEffect(() => {
    const handleTyping = () => {
      const currentSentence = sentences[sentenceIndex];
      if (!isDeleting) {
        if (index < currentSentence.length) {
          setText(text + currentSentence.charAt(index));
          setIndex(index + 1);
        } else {
          setTimeout(() => setIsDeleting(true), 1000); // Pause before starting to delete
        }
      } else {
        if (index > 0) {
          setText(currentSentence.substring(0, index - 1));
          setIndex(index - 1);
        } else {
          setIsDeleting(false);
          setSentenceIndex((sentenceIndex + 1) % sentences.length);
        }
      }
    };

    const timeoutId = setTimeout(handleTyping, speed);

    return () => clearTimeout(timeoutId);
  }, [text, isDeleting, index, sentenceIndex, sentences, speed]);

  useEffect(() => {
    setSpeed(isDeleting ? 50 : 150); // Adjust speed when deleting
  }, [isDeleting]);

  return (
    <div className="typing-container">
      <span>{text}</span>
      <span className="cursor">|</span>
    </div>
  );
};

export default TypingAnimation;
