// src/TypingAnimation.js
import React, { useState, useEffect } from "react";
import "./TypingAnimation.css";

const TypingAnimation = () => {
  const sentences = ["A Programmer", "A Web Developer", "A Data Scientist", "An App Developer", "A Game Developer"];

  const [text, setText] = useState("");
  const [index, setIndex] = useState(0);
  const [sentenceIndex, setSentenceIndex] = useState(0);
  const [isDeleting, setIsDeleting] = useState(false);
  const [speed, setSpeed] = useState(150);

  useEffect(() => {
    const handleTyping = () => {
      const currentSentence = sentences[sentenceIndex];
      if (!isDeleting) {
        if (index < currentSentence.length) {
          setText(text + currentSentence.charAt(index));
          setIndex(index + 1);
        } else {
          setTimeout(() => setIsDeleting(true), 1000);
        }
      } else {
        if (index > 0) {
          setText(currentSentence.substring(0, index - 1));
          setIndex(index - 1);
        } else {
          setIsDeleting(false);
          setSentenceIndex((sentenceIndex + 1) % sentences.length);
        }
      }
    };

    const timeoutId = setTimeout(handleTyping, speed);

    return () => clearTimeout(timeoutId);
  }, [text, isDeleting, index, sentenceIndex, sentences, speed]);

  useEffect(() => {
    setSpeed(isDeleting ? 50 : 150);
  }, [isDeleting]);

  return (
    <div className="typing-container">
      {/* <span>{text}</span> */}
      <span className="cursor">|</span>
    </div>
  );
};

export default TypingAnimation;
