// src/components/LockWheel.js
import React, { useState } from "react";
import "./logo.css";

const LockWheel = ({ position }) => {
  const moveTo = -(position * 60);
  return (
    <div className="lock">
      <div className="wheel" style={{ transform: `translateY(${moveTo}px)` }}>
        {[...Array(10).keys()].map((num) => (
          <div key={num}>{num}</div>
        ))}
      </div>
    </div>
  );
};

const Logo = () => {
  const [positions, setPositions] = useState([0, 0, 0]);

  const targetCombination = [3, 5, 7];

  const handleMatch = () => {
    setPositions(targetCombination);
  };

  return (
    <div className="lock-container">
      {positions.map((position, index) => (
        <LockWheel key={index} position={position} />
      ))}
      <button onClick={handleMatch}>Match Password</button>
    </div>
  );
};

export default Logo;
