import React, { useEffect, useState } from "react";
import "./logo.css";

const Logo = () => {
  const [positions, setPositions] = useState([0, 0, 0]);

  const targetCombination = [3, 5, 7];

  const handleMatch = () => {
    setPositions(targetCombination);
  };

  useEffect(() => {
    const logoWheel = document.getElementsByClassName("logo-wheel");
    logoWheel[0].style.transform = `translateY(${60}px);`;
    // for (let index = 0; index < logoWheel.length; index++) {
    //   logoWheel[index].style.transform = `translateY(${10 * index}%);`;
    // }
  }, []);

  return (
    <div className="logo">
      <div>{"<"}</div>
      <div className="logo-wheel">F A R I A</div>
      <div className="logo-wheel">F A R I A</div>
      <div className="logo-wheel">F A R I A</div>
      <div className="logo-wheel">F A R I A</div>
      <div className="logo-wheel">F A R I A</div>
      <div>{"/>"}</div>
    </div>
  );
};

export default Logo;
